.header-space {
  height: 10vh;
}

#theme-links {
  display: block;
  margin: 0 3% 20vh;
  padding: 0rem 0 5vh;

  background-color: rgba(50, 50, 50, 0.6);

  border-radius: 1rem;

  #link-title {
    color: rgb(240, 240, 240);
    font-size: 2.5rem;
    font-weight: 600;
    line-height: 5rem;
  }

  .link {
    border-bottom: 0.1rem dashed rgb(230, 230, 230);

    &#link-top {
      border-top: 0.1rem dashed rgb(230, 230, 230);
    }

    div {
      text-align: left;
      padding: 0 1rem 0 0;

      transition: 0.4s;

      line-height: 8vh;

      p {
        color: rgb(240, 240, 240);
        vertical-align: middle;
        display: inline-block;
        font-size: 1.6rem;
        margin: 0 0;

        // @media only screen and (max-width: 500px) {
        //   font-size: 1.6rem;
        // }
        // @media only screen and (min-width: 500px) {
        //   font-size: 1.6rem;
        // }

        transition: 0.4s;

        &.icon {
          vertical-align: middle;
          font-size: 2.6rem;
          margin: 0 1rem;
        }
      }

