.header-space {
  height: 10vh;
}

#theme-link {
  display: block;
  height: 80vh;
  margin-bottom: 20vh;
  padding: 0 5%;

  #link-grid {
    display: grid;
    height: 100%;
    width: 100%;
    grid-template-rows: 100%;
    grid-template-columns: 20% 20% 20% 20% 20%;

    .grid-item {
      // border: 2px solid #cc0000;
      display: flex;
      align-items: center;
      background: linear-gradient(
        rgba(255, 255, 255, 0.2) 50%,
        rgba(0, 0, 0, 0.5)
      );
      color: #4a4a4a;
      margin: 0px 5%;
      box-shadow: 0.3rem 0.3rem 0.3rem rgba(0, 0, 0, 0.5);
      text-decoration: none;
      transition: 0.3s;

      writing-mode: vertical-rl;
      -ms-writing-mode: tb-rl;
      -webkit-text-orientation: upright;
      text-orientation: upright;
      p {
        font-size: 2rem;
        height: 90%;
        margin: 0 auto;
      }

      &::after {
        content: "";
        display: inline-block;
        width: 90%;
        height: 7%;
        background-size: 100% 100%;
        vertical-align: middle;
        background-image: url("../assets/lab/theme/black-arrow-down.svg");
        transition-duration: 0.5s;
      }

      &:hover {
        background: linear-gradient(rgba(80, 80, 80, 0.8), rgba(0, 0, 0, 0.9));
        color: #efefef;
        box-shadow: 0.1rem 0.1rem 0.2rem rgba(0, 0, 0, 0.8);

        p {
          font-weight: 800;
        }

        &::after {
          background-image: url("../assets/lab/theme/white-arrow-down.svg");
        }
      }

      &#item1 {
        grid-row: 1;
        grid-column: 1;
      }

      &#item2 {
        grid-row: 1;
        grid-column: 2;
      }

      &#item3 {
        grid-row: 1;
        grid-column: 3;
      }

      &#item4 {
        grid-row: 1;
        grid-column: 4;
      }

      &#item5 {
        grid-row: 1;
        grid-column: 5;
      }
    }
  }
}

.theme-group {
  width: 100%;
  padding: 10vh 10vw;
  position: relative;
  transition: 1.2s;

  .theme-name {
    line-height: 3.3rem;
    font-size: 2rem;
    position: absolute;
    top: 5vh;
    left: 5vw;
    background-color: #ffffff;
    border-left: 0.6rem solid rgba(20, 20, 20, 0.8);
    border-bottom: 0.2rem solid rgba(20, 20, 20, 0.8);
    box-shadow: 0.3rem 0.3rem 0.1rem rgba(0, 0, 0, 0.3);
    padding-left: 1.1rem;
    padding-right: 1.1rem;
  }
  img {
    width: 100%;
    display: inline-block;
    background: url("../assets/lab/theme/2019_BP.png");
  }
}

.scroll-Y {
  position: fixed;
  color: #ff0000;
}
